5;); &#125;); &#125; function getCurrentSite() &#123; const url = window.location.href; if(url.includes(&#39;aistudio.google.com&#39;)) return CONFIG.sites.GEMINI.id; if(url.includes(&#39;chatgpt.com&#39;)) return CONFIG.sites.CHATGPT.id; if(url.includes(&#39;claude.ai&#39;)) return CONFIG.sites.CLAUDE.id; if(url.includes(&#39;chat.deepseek.com&#39;)) return CONFIG.sites.DEEPSEEK.id; if(url.includes(&#39;grok.com&#39;)) return CONFIG.sites.GROK.id; &#125; function createElement(tag, attributes = &#123;&#125;) &#123; const element = document.createElement(tag); Object.entries(attributes).forEach(([key, value]) =&gt; &#123; if (key === &#39;className&#39;) &#123; element.className = value; &#125; else if (key === &#39;textContent&#39;) &#123; element.textContent = value; &#125; else if (key === &#39;style&#39; &amp;&amp; typeof value === &#39;object&#39;) &#123; Object.assign(element.style, value); &#125; else if (key === &#39;onclick&#39;) &#123; element.addEventListener(&#39;click&#39;, value); &#125; else if (key.startsWith(&#39;data-&#39;)) &#123; element.setAttribute(key, value); &#125; else if (key === &#39;html&#39;) &#123; element.innerHTML = value; &#125; else if (key === &#39;on&#39; &amp;&amp; typeof value === &#39;object&#39;) &#123; Object.entries(value).forEach(([event, handler]) =&gt; &#123; element.addEventListener(event, handler); &#125;); &#125; else &#123; element.setAttribute(key, value); &#125; &#125;); return element; &#125; function filterPromptsByBlacklist(prompts) &#123; const blacklist = new Set(CONFIG.sync.blacklist); const filteredPrompts = &#123;&#125;; for (const [id, content] of Object.entries(prompts)) &#123; if (!blacklist.has(id)) &#123; filteredPrompts[id] = content; &#125; else &#123; debug.log(`ÊèêÁ§∫ËØç &quot;$&#123;id&#125;&quot; Âú®ÈªëÂêçÂçï‰∏≠ÔºåÂ∑≤ËøáÊª§`); &#125; &#125; return filteredPrompts; &#125; function syncPrompts(storedPrompts, initialPromptsObject) &#123; if (!CONFIG.sync.enabled) &#123; debug.log(&#39;ÊèêÁ§∫ËØçÂêåÊ≠•ÂäüËÉΩÂ∑≤Á¶ÅÁî®&#39;); return storedPrompts; &#125; debug.log(&#39;ÊèêÁ§∫ËØçÂ∫ìÂêåÊ≠•‰∏≠...&#39;); const filteredInitialPrompts = filterPromptsByBlacklist(initialPromptsObject); let hasNewPrompts = false; const syncedPrompts = &#123; ...storedPrompts &#125;; for (const [id, content] of Object.entries(filteredInitialPrompts)) &#123; if (!syncedPrompts[id]) &#123; debug.log(`ÂèëÁé∞Êñ∞ÊèêÁ§∫ËØç: &quot;$&#123;id&#125;&quot;`); syncedPrompts[id] = content; hasNewPrompts = true; &#125; &#125; if (hasNewPrompts) &#123; debug.log(&#39;ÂêåÊ≠•ÂÆåÊàêÔºåÂèëÁé∞Âπ∂Ê∑ªÂä†‰∫ÜÊñ∞ÁöÑÊèêÁ§∫ËØç&#39;); GM_setValue(&#39;prompts&#39;, syncedPrompts); &#125; else &#123; debug.log(&#39;ÂêåÊ≠•ÂÆåÊàêÔºåÊ≤°ÊúâÂèëÁé∞Êñ∞ÁöÑÊèêÁ§∫ËØç&#39;); &#125; return syncedPrompts; &#125; function loadPrompts() &#123; debug.log(&#39;Ê≠£Âú®ËΩΩÂÖ•ÊèêÁ§∫ËØç...&#39;); const storedPrompts = GM_getValue(&#39;prompts&#39;); if (!storedPrompts) &#123; const filteredPrompts = filterPromptsByBlacklist(promptsObject); savedPrompts = filteredPrompts; debug.log(&#39;Êú™ÂèëÁé∞ GM Â≠òÂÇ®‰∏≠ÁöÑÊèêÁ§∫ËØçÔºåÂ∞Ü‰ΩøÁî®ÂàùÂßãÂåñÊó∂Âä†ËΩΩÁöÑÈªòËÆ§ÊèêÁ§∫ËØç:&#39;, savedPrompts); GM_setValue(&#39;prompts&#39;, savedPrompts); &#125; else &#123; debug.log(&#39;ÂèëÁé∞ GM Â≠òÂÇ®‰∏≠Â∑≤ÊúâÊèêÁ§∫ËØçÂ¶Ç‰∏ã&#39;, storedPrompts); savedPrompts = syncPrompts(storedPrompts, promptsObject); &#125; return savedPrompts; &#125; function saveNewPrompt(id, content) &#123; savedPrompts[id] = content; GM_setValue(&#39;prompts&#39;, savedPrompts); updateDatalist(); debug.log(&#39;Êñ∞ÁöÑÊèêÁ§∫ËØçÂ∑≤‰øùÂ≠ò:&#39;, id); &#125; async function setPromptWithRetry(site, prompt, maxRetries = CONFIG.maxRetries) &#123; return new Promise(async (resolve, reject) =&gt; &#123; let attempts = 0; const trySetPrompt = async () =&gt; &#123; try &#123; const config = Object.values(CONFIG.sites).find(s =&gt; s.id === site); if(!config || !config.setPrompt) &#123; return reject(new Error(`Áº∫Â∞ëÂΩìÂâçÁ´ôÁÇπÂØπÂ∫îÁöÑÊñáÊú¨ÁºñËæëÂô®ÈÖçÁΩÆ: $&#123;site&#125;`)); &#125; const e