ONVERT = shutil.which(&quot;ebook-convert&quot;) is not None def run(cmd): subprocess.run(cmd, check=True, stdout=subprocess.DEVNULL, stderr=subprocess.DEVNULL) def prepare_dirs(): TEMP_DIR.mkdir(exist_ok=True) TITLED_DIR.mkdir(exist_ok=True) def clean_dirs(): shutil.rmtree(TEMP_DIR, ignore_errors=True) shutil.rmtree(TITLED_DIR, ignore_errors=True) def office_to_pdf(src: Path) -&gt; Path: if not HAS_SOFFICE: raise RuntimeError(&quot;未检测到 LibreOffice（soffice）&quot;) run([&quot;soffice&quot;, &quot;--headless&quot;, &quot;--convert-to&quot;, &quot;pdf&quot;, str(src), &quot;--outdir&quot;, str(TEMP_DIR)]) return TEMP_DIR / f&quot;&#123;src.stem&#125;.pdf&quot; def image_to_pdf(src: Path) -&gt; Path: pdf_path = TEMP_DIR / f&quot;&#123;src.stem&#125;.pdf&quot; Image.open(src).convert(&quot;RGB&quot;).save(pdf_path) return pdf_path def ebook_to_pdf(src: Path) -&gt; Path: if not HAS_EBOOK_CONVERT: raise RuntimeError(&quot;未检测到 ebook-convert（Calibre）&quot;) pdf_path = TEMP_DIR / f&quot;&#123;src.stem&#125;.pdf&quot; run([&quot;ebook-convert&quot;, str(src), str(pdf_path)]) return pdf_path def create_title_page(title: str, output: Path): c = canvas.Canvas(str(output)) c.setFont(&quot;Helvetica-Bold&quot;, 24) c.drawString(72, 720, f&quot;# &#123;title&#125;&quot;) c.save() def prepend_title_page(pdf: Path) -&gt; Path: title_pdf = pdf.with_suffix(&quot;.title.pdf&quot;) create_title_page(pdf.name, title_pdf) merger = PdfMerger() merger.append(str(title_pdf)) merger.append(str(pdf)) output = TITLED_DIR / pdf.name merger.write(str(output)) title_pdf.unlink(missing_ok=True) return output def main(): if not INPUT_DIR.exists(): print(&quot;❌ 输入目录不存在&quot;) return prepare_dirs() processed = [] for file in sorted(INPUT_DIR.iterdir()): if not file.is_file(): continue ext = file.suffix.lower() try: if ext == &quot;.pdf&quot;: pdf = TEMP_DIR / file.name shutil.copy(file, pdf) elif ext in IMAGE_EXTS: pdf = image_to_pdf(file) elif ext in OFFICE_EXTS: pdf = office_to_pdf(file) elif ext in EBOOK_EXTS: pdf = ebook_to_pdf(file) else: continue processed.append(prepend_title_page(pdf)) except Exception as e: print(f&quot;[失败] &#123;file.name&#125;: &#123;e&#125;&quot;) if not processed: print(&quot;⚠️ 没有成功处理任何文件&quot;) return merger = PdfMerger() for pdf in processed: merger.append(str(pdf)) merger.write(OUTPUT_FILE) print(f&quot;
✅ 已生成：&#123;OUTPUT_FILE&#125;&quot;) if __name__ == &quot;__main__&quot;: clean_dirs() main() 尝试Vibe Coding下面的提示词适合没有编程背景的同学来尝试vibe coding，纯大白话，写这样的提示词没有任何难度。提示词： 我没有编程经验，帮我写一个可以直接用的 Python 脚本。 我的需求： 1. 我有一个文件夹，里面放着很多资料文件 可能包括： - PDF - Word 文档 - PPT - Excel - 图片（jpg、png） （不一定每种都有） 2. 我希望用一个脚本，把这个文件夹里的所有文件： - 自动整理 - 最终合并成一个 PDF 文件 3. 合并后的 PDF 要满足： - 每一个原始文件，在 PDF 里都有一个明显的标题 - 标题内容就是原始文件名 4. 使用体验要求： - 我不懂代码，只能照着步骤操作 - 需要手动配置的内容放到脚本开头（比如文件夹路径） - 不需要我自己研究技术细节 5. 关于环境和工具： - 我只知道需要安装 Python - 其他需要的东西，请你帮我考虑好 - 如果我电脑上没有某些工具，也不要让程序直接报错崩掉 - 能处理的文件就处理，不能处理的就跳过并告诉我 - 输出详细的日志信息，以便于我遇到错误时发送给AI进行分析 6. 输出要求： - 请直接给我一整段可以运行的 Python 代码 - 再用非常简单的话，告诉我： - 第一步做什么 - 第二步做什么 - 每一步是为了什么 - 不要假设我懂编程术语

